        console.log(e)
    }), e = $("#sale-form"), $(e).find("select").change(function() {
        $(".modal-form__select_option").val($(e).find("select option:selected").text().replace("@lang('contact.your_city')", ""))
    }), e.on("submit", function(e) {
        e.preventDefault();
        let t = $(this),
            o = t.find('[name="phone"]').val();
        if (null !== t.find('[name="city"]').val() && o.replace(/[^+\d]/g, "").length > 11) {
            t.serialize(), t.find("[type='submit']").attr("disabled", !0);
            let e = $.ajax({
                url: "/lol",
                type: "post",
                data: {
                    name: t.find('[name="name"]').val(),
                    _token: t.find('[name="_token"]').val(),
                    city: t.find('[name="city"]').val(),
                    phone: t.find('[name="phone"]').val(),
                    type: t.find('[name="type"]').val(),
                    region: t.find('[name="region"]').val(),
                    visit: "{{$visit}}"
                }
            });
            return e.done(function(e, o, n) {
                console.log(e), t.find('[name="phone"]').val(""), t.find('[name="name"]').val(""), t.find('[name="message"]').val(""), t.find("[data-error-message]").hide(), Cookies.set("show-success", "1"), window.location.href = "/success"
            }), e.fail(function(e, o, n) {
