    <style>
        .contact-info {
            max-width: 70rem;
            margin-left: 7rem;
        }

        a.cont {
            color: #ffae1f;
            text-decoration: underline;
        }

        .title-box {
            display: flex;
            justify-content: space-between
        }

        .price-bl {
            position: relative;
            padding: 30px;
            height: 230px;
            background: #EDF9F7;
        }


        .price-txt-bl {
        }

        .col-33-2 {
            width: 32%;
            margin-bottom: 20px;
            border-radius: 10px;
        }

        .price-txt-bl .price-title {
            font-size: 24px;
            text-align: left;
            color: #2DBE60;
            font-weight: 700;
            margin-bottom: 20px;
            margin-top: 0;
            text-transform: capitalize;
        }

        .price-txt-bl .price-info-text {
            color: #828282;
            text-align: left;
            width: 80%;
            font-size: 15px;
        }

        .price-logo {
            position: absolute;
            right: 5px;
            bottom: 0;
        }


        @media(max-width: 767px) {
            .contact-info {
                margin-left: 0!important
            }

            .title-box {
                display: block!important;
            }

            .contact-info .lead {
                margin-bottom: 4rem!important;
            }
        }
    </style>
    <script>
        const prices = {
            oneStorePrice: 4000,
            oneEmployeePrice: 1500,
            oneCashboxPrice: 2000,
            setup: 30000
        };
        function money(n) {
            n += "";
            n = new Array(4 - n.length % 3).join("U") + n;
            return n.replace(/([0-9U]{3})/g, "$1 ").replace(/U/g, "");
        }
        function countResultPrice() {
            let numberOfStores = $('#number-of-stores'),
                numberOfEmployees = $('#number-of-employees'),
                numberOfCashboxes = $('#number-of-cashboxes');
            let numberOfStoresVal = parseInt(numberOfStores.val()),
                numberOfEmployeesVal = parseInt(numberOfEmployees.val()),
                numberOfCashboxesVal = parseInt(numberOfCashboxes.val());
            if (Number.isNaN(numberOfStoresVal) || numberOfStoresVal < 1) numberOfStoresVal = 1;
            if (Number.isNaN(numberOfEmployeesVal) || numberOfEmployeesVal < 1) numberOfEmployeesVal = 1;
            if (Number.isNaN(numberOfCashboxesVal) || numberOfCashboxesVal < 1) numberOfCashboxesVal = 1;
            numberOfStores.val(numberOfStoresVal);
            numberOfEmployees.val(numberOfEmployeesVal);
            numberOfCashboxes.val(numberOfCashboxesVal);
            let result = (numberOfStoresVal * prices.oneStorePrice)
                + (numberOfEmployeesVal * prices.oneEmployeePrice)
                + (numberOfCashboxesVal * prices.oneCashboxPrice);
            let resultSetup = numberOfStoresVal * prices.setup
            $('[data-calculator-result-monthly]').text(money(result));
            $('[data-calculator-result-annual]').text((money(result*12)));
            $('[data-calculator-result-support]').text(money(resultSetup));
            $('[data-calculator-result-final]').text(money((result*12)+resultSetup));
            $('[data-calculator-result-semi-annual]').text((money(result*6)));
            $('[data-calculator-result-semi-support]').text(money(resultSetup));
            $('[data-calculator-result-semi-final]').text(money((result*6)+resultSetup));
        }
        $('[data-calculator-input]').on('input', function() {
            countResultPrice();
        });
        countResultPrice();
        $('[data-calculator-plus]').on('click', function(e) {
            e.stopPropagation();
            let inputId = $(this).data("calculator-plus");
            let input = $(inputId);
            input.val(parseInt(input.val()) + 1);
            countResultPrice();
        });
        $('[data-calculator-minus]').on('click', function(e) {
            e.stopPropagation();
            let inputId = $(this).data("calculator-minus");
            let input = $(inputId);
            if (parseInt(input.val()) > 1) input.val(parseInt(input.val()) - 1);
            countResultPrice();
        });
    </script>
